#!/bin/sh
while true; do VALEUR1=$(/usr/bin/curl --interface wlan-sta --dns-servers 8.8.8.8 --dns-interface wlan-sta http://www.google.com/ | grep -c 'success'); echo "result: $VALEUR1"; if [ $VALEUR1 == 0 ] ; then echo "Login"; curl --interface wlan-sta --dns-servers 8.8.8.8 --dns-interface wlan-sta 'https://hautdebitmobile.orange.fr:8443/home/wassup?isCgu=on&hidden_isCgu=&doCheckCgu=1' -H 'Connection: keep-alive' -H 'Upgrade-Insecure-Requests: 1' -H 'DNT: 1' -H 'User-Agent: Mozilla/5.0 (Macintosh; Intel Mac OS X 10_15_2) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/80.0.3987.149 Safari/537.36' -H 'Sec-Fetch-Dest: document' -H 'Accept: text/html,application/xhtml+xml,application/xml;q=0.9,image/webp,image/apng,*/*;q=0.8,application/signed-exchange;v=b3;q=0.9' -H 'Sec-Fetch-Site: same-origin' -H 'Sec-Fetch-Mode: navigate' -H 'Sec-Fetch-User: ?1' -H 'Referer: https://hautdebitmobile.orange.fr:8443/home' -H 'Accept-Language: fr-FR,fr;q=0.9,en-GB;q=0.8,en;q=0.7,ar-XB;q=0.6,ar;q=0.5,en-US;q=0.4' -H 'Cookie: izclientid=<REPLACE WITH YOURS>; JSESSIONID=<REPLACE WITH YOURS>; wassup=<REPLACE WITH YOURS>; owaUserName=<REPLACE WITH YOURS>' ; else printf 'Fully Operational'; fi; sleep 3; done
